// make filename to random number
function hashFilename(filename) {
	let hash = 0;
	Array.from(filename).map((key, index) => {
		hash +=
			Math.pow(key.charCodeAt(0), index) *
			Math.pow(41, filename.length - 1 - index);
	});

	return hash % 10000;
}

